import { Router } from "express";
import authenticationService from "./auth.service.js";
import validationMiddleware from "../../middlewares/validation.middleware.js";
import AuthValidators from "./auth.validation.js";
const authRouter = Router();
authRouter.post("/signup", validationMiddleware(AuthValidators.signup), authenticationService.signup);
authRouter.post("/login", validationMiddleware(AuthValidators.login), authenticationService.login);
authRouter.patch("/confirm-email", validationMiddleware(AuthValidators.confirmEmail), authenticationService.confirmEmail);
authRouter.patch("/resend-email-otp", validationMiddleware(AuthValidators.resendEmilOtp), authenticationService.resendEmilOtp);
authRouter.patch("/send-forget-password-otp", validationMiddleware(AuthValidators.sendForgetPasswordOtp), authenticationService.sendForgetPasswordOtp);
authRouter.patch("/verify-forget-password-otp", validationMiddleware(AuthValidators.verifyForgetPasswordOtp), authenticationService.verifyForgetPasswordOtp);
authRouter.patch("/reset-forgot-password", validationMiddleware(AuthValidators.resetForgotPassword), authenticationService.resetForgotPassword);
export default authRouter;
